import React, { useEffect, useState } from "react";
import "./map.css";
import {
  Container,
  Box,
  Button,
  Grid,
  RadioGroup,
  FormControlLabel,
  FormControl,
  Radio,
} from "@material-ui/core";
const { kakao } = window;
let infowindow = new kakao.maps.InfoWindow({ zIndex: 1 });
let lat = "";
let lon = "";

const MapContainer = ({ searchPlace }) => {
  const [gps, setGps] = useState([]);
  const [map, setMap] = useState({});

  function getGPS() {
    const userLocation = [];

    if (navigator.geolocation) {
      console.log("hi");
      navigator.geolocation.getCurrentPosition(function (position) {
        console.log("hi");
        lat = position.coords.latitude; // 위도
        lon = position.coords.longitude; // 경도
        const container = document.getElementById("map1");
        const options = {
          center: new kakao.maps.LatLng(lat, lon),
          level: 3,
        };
        userLocation.push(lat);
        userLocation.push(lon);
        setGps(userLocation);
        setMap(new kakao.maps.Map(container, options));
      });
    }
  }

  useEffect(() => {
    getGPS();
  }, []);
  console.log(gps);

  function placesSearchCB(data, status, pagination) {
    if (status === kakao.maps.services.Status.OK) {
      let bounds = new kakao.maps.LatLngBounds();

      for (let i = 0; i < data.length; i++) {
        displayMarker(data[i]);
        bounds.extend(new kakao.maps.LatLng(data[i].y, data[i].x));
      }

      map.setBounds(bounds);
    }
  }

  function displayMarker(place) {
    let marker = new kakao.maps.Marker({
      map: map,
      position: new kakao.maps.LatLng(place.y, place.x),
      image: new kakao.maps.MarkerImage(
        "https://cdn3.iconfinder.com/data/icons/flat-pro-basic-set-1-1/32/location-blue-256.png",
        new kakao.maps.Size(31, 35),
        {
          offset: new kakao.maps.Point(16, 34),
          alt: "마커 이미지",
        }
      ),
    });

    kakao.maps.event.addListener(marker, "mouseover", function () {
      // 마커를 클릭하면 장소명이 인포윈도우에 표출
      infowindow.setContent(
        '<div class="info" >' +
          "   <h6>" +
          place.place_name +
          "</h6>" +
          "    <span>" +
          place.road_address_name +
          "</span>" +
          "</br>" +
          '   <span class="jibun gray">' +
          place.address_name +
          "</span>" +
          "</br>" +
          '  <span class="tel">' +
          place.phone +
          "</span>" +
          "</div>"
      );
      infowindow.open(map, marker);
    });
    kakao.maps.event.addListener(marker, "mouseout", function () {
      infowindow.close();
    });
  }

  useEffect(() => {
    if (gps != [] && map != {} && searchPlace) {
      const ps = new kakao.maps.services.Places();
      ps.keywordSearch(searchPlace, placesSearchCB, {
        location: new kakao.maps.LatLng(gps[0], gps[1]),
        bounds: new kakao.maps.LatLngBounds(
          new kakao.maps.LatLng(gps[0] - 0.1, gps[1] - 0.1),
          new kakao.maps.LatLng(gps[0] + 0.1, gps[1] + 0.1)
        ),
        radius: 10000,
      });
    }
  }, [map, gps, searchPlace]);

  return (
    <Grid container justify="center">
      <div
        id="map1"
        style={{
          width: "350px",
          height: "350px",
        }}
      ></div>
    </Grid>
  );
};

export default MapContainer;
