import React, {useState} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import {useHistory, useLocation} from "react-router";

export default function CounselorLogin(){
    const history = useHistory();
    const [counselorid, setCounselorId] = useState('');
    const [counselorpassword, setCounselorPassword] = useState('');

    const moveToCounselorSignup = () =>{
        history.push({
            pathname: "/CounselorSignup",
        });
    };

    const loginToChatCounselor = () =>{
        history.push({
            pathname: "/ChatCounselor",
        })
    };

    return(
        <div>
            <h1>
                상담사 로그인 페이지
            </h1>

            <Form.Group controlId="counselorId">
                <Form.Label>아이디</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="아이디를 입력해주세요"
                    onChange={(e) => {
                        setCounselorId(e.target);
                    }}
                ></Form.Control>
            </Form.Group>

            <Form.Group controlId="counselorPassword">
                <Form.Label>비밀번호</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="비밀번호를 입력해주세요"
                    onChange={(e) => {
                        setCounselorPassword(e.target);
                    }}
                ></Form.Control>
            </Form.Group>

            <Button varient="primary" onClick={moveToCounselorSignup}>회원가입</Button>
            <Button varient="primary" onClick={loginToChatCounselor}>로그인</Button>
        </div>
    );
}
